def flowFile = session.get()
if (!flowFile) return

def tablename     = flowFile.getAttribute("tablename")
def columnAttr    = flowFile.getAttribute("columns")  // ← korrigiert
def datatypeAttr  = flowFile.getAttribute("datatypes")
def pkeys         = flowFile.getAttribute("pkey")

// Feste Schemazuweisung
def schema = "staging"

// Nullprüfungen
if (!tablename || !columnAttr || !datatypeAttr) {
    log.error("Missing required attributes: tablename=${tablename}, columnnames=${columnAttr}, datatypes=${datatypeAttr}")
    session.transfer(flowFile, REL_FAILURE)
    return
}

def columns = columnAttr.split(",")
def types   = datatypeAttr.split(",")

if (columns.length != types.length) {
    log.error("Mismatch between column count (${columns.length}) and type count (${types.length})")
    session.transfer(flowFile, REL_FAILURE)
    return
}

// Spalten zusammensetzen
def colDefs = columns.indices.collect { i -> "${columns[i]} ${types[i]}" }.join(", ")

// Primärschlüssel (optional)
if (pkeys?.trim()) {
    colDefs += ", PRIMARY KEY (${pkeys})"
}

// SQL-Statement
def sql = """DROP TABLE IF EXISTS ${schema}.${tablename};
CREATE TABLE ${schema}.${tablename} (
  ${colDefs}
);
"""

// Inhalt setzen
flowFile = session.write(flowFile, { out ->
    out.write(sql.getBytes("UTF-8"))
} as OutputStreamCallback)

// Debug optional
log.info("Generated SQL: \n" + sql)

// Weitergeben
session.transfer(flowFile, REL_SUCCESS)
